import random

def generate_insert_statements(num_records, vendas_soma):
    statements = []
    for _ in range(num_records):
        item_id, nome, quantidade_total = random.choice(vendas_soma)
        statement = (
            f"INSERT INTO agregados_vendas (item_id, nome, quantidade_total) "
            f"VALUES ({item_id}, '{nome}', {quantidade_total});"
        )
        statements.append(statement)
    return statements

